import { ADD_TODO } from "../types";

const INITIAL_TODO = {
  text: ""
};

export default function reducer(state = INITIAL_TODO, action) {
  switch (action.type) {
    case ADD_TODO:
      return state.concat(action.payload);
    default:
      return state;
  }
}
