import { ADD_TODO } from "../types";

const INITIAL_TODO = {
  payload: "",
  id: action.id,
  completed: false
};

export default function reducer(state = INITIAL_TODO, action) {
  switch (action.type) {
    case ADD_TODO:
      return [
        ...state,
        { id: action.id, payload: action.payload, completed: false }
      ];
    default:
      break;
  }
}
