import { ADD_TODO, ADD_CHECKED, ADD_DELETE, ALL_ACTIVE } from "../types";

let id = 0;

export default function reducer(state = [], action) {
  var active = state;
  switch (action.type) {
    case ADD_TODO:
      return state.concat({
        todotext: action.payload,
        completed: false,
        id: id++
      });

    case ADD_CHECKED:
      return state.map(event => {
        if (event.id == action.payload) {
          event.completed = !event.completed;
        }
        return event;
      });
    case ADD_DELETE:
      return state.filter(event => event.id !== action.payload);
    case ALL_TODO:
      return state;
    case ALL_ACTIVE:
      return active.filter(event => event.completed == false);
    default:
      return state;
  }
}
