import React from "react";
import { connect } from "react-redux";
import { addTodo } from "../actions";

class Todo extends React.Component {
  handleChange = event => {
    if (event.keyCode == 13) {
      this.props.dispatch(addTodo(event.target.value));
      event.target.value = "";
    }
  };

  render() {
    let { todo, dispatch } = this.props;
    return (
      <div>
        <center>
          <h1 className="todo-heading">Todos</h1>
          <input
            className="todo-input"
            type="text"
            placeholder="Enter your todo"
            value={this.state.todotext}
            onKeyDown={this.handleChange}
          />
          {/* <button
            className="add-button"
            onClick={() => dispatch(addTodo(this.state.todotext))}
          >
            Add Todo
          </button> */}
        </center>
        <ul>
          {todo.map(item, index => (
            <li className="todolist">
              <div className="list-div">
                <div className="checkbox-text">
                  <input type="checkbox" />
                  <h4 className="todo-text">{item.todotext}</h4>
                </div>
                <div>
                  <button className="cut-button">x</button>
                </div>
              </div>
            </li>
          ))}
        </ul>
      </div>
    );
  }
}
function mapStateToProps(state) {
  console.log(state);
  return { todo: state };
}

export default connect(mapStateToProps)(Todo);
