import { ADD_TODO } from "../types";

const INITIAL_TODO = {
  todo: {
    payload: "",
    id: 0,
    completed: false
  }
};

export default function reducer(state = INITIAL_TODO, action) {
  switch (action.type) {
    case ADD_TODO:
      return [...state, { todo: payload }];
    default:
      break;
  }
}
